import React from "react";
import {
	LineChart,
	Line,
	XAxis,
	YAxis,
	CartesianGrid,
	Tooltip,
	Legend,
	ResponsiveContainer,
	BarChart,
	Bar,
	Cell,
} from "recharts";

const getPath = (x, y, width, height) => {
	return `M${x},${y + height}C${x + width / 3},${
		y + height
	} ${x + width / 2},${y + height / 3}
	${x + width / 2}, ${y}
	C${x + width / 2},${y + height / 3} ${
		x + (2 * width) / 3
	},${y + height} ${x + width}, ${y + height}
	Z`;
};
const TriangleBar = (props) => {
	const { fill, x, y, width, height } = props;

	return (
		<path
			d={getPath(x, y, width, height)}
			stroke="none"
			fill={fill}
		/>
	);
};
const colors = [
	"#0088FE",
	"#00C49F",
	"#FFBB28",
	"#FF8042",
	"red",
	"pink",
];

function Activities() {
	return (
		<div className=" pl-4 activities gap-3 p-7 h-full w-full">
			<div className="bg-sky-400 "> helo </div>
			<div className="bg-zinc-400 "> helo </div>
			<div className="bg-green-400  "> helo </div>
			<div className="col-span-full flex gap-2 ">
				{/* <div className="h-full backdrop-blur-md flex-1 w-full">
					<ResponsiveContainer
						width="100%"
						height="100%"
					>
						<LineChart
							data={most_view_post}
							height={20}
							width={20}
							margin={{}}
						>
							<CartesianGrid strokeDasharray="3 3" />
							<XAxis dataKey="name" />
							<YAxis />
							<Tooltip />
							<Legend />
							<Line
								type="monotone"
								dataKey="number_of_viewers"
								stroke="#8884d8"
								activeDot={{ r: 8 }}
							/>
						</LineChart>
					</ResponsiveContainer>
				</div> */}
				<div className="bg-white justify-center items-center h-full flex-1 w-full">
					<div className="m-auto p-4 bg-red-500">
						<BarChart
							width={500}
							height={300}
							data={most_view_post}
						>
							<CartesianGrid strokeDasharray="3 3" />
							<XAxis dataKey="name" />
							<YAxis />
							<Bar
								dataKey="number_of_viewers"
								fill="#8884d8"
								shape={<TriangleBar />}
								label={{ position: "top" }}
							>
								{most_view_post.map((entry, index) => (
									<Cell
										key={`cell-${index}`}
										fill={colors[index % 20]}
									/>
								))}
							</Bar>
						</BarChart>
					</div>
				</div>
			</div>
		</div>
	);
}

export default Activities;

const most_view_post = [
	{
		id: 1,
		category: " education",
		number_of_viewers: 40,
	},
	{
		id: 2,
		category: " Religin",
		number_of_viewers: 40,
	},
	{
		id: 3,
		category: " technology",
		number_of_viewers: 89,
	},
	{
		id: 4,
		category: " Agriculture",
		number_of_viewers: 200,
	},
	{
		id: 5,
		category: "Politics",
		number_of_viewers: 37,
	},
];
