import React from "react";
import {
	LineChart,
	Line,
	XAxis,
	YAxis,
	CartesianGrid,
	Tooltip,
	Legend,
	ResponsiveContainer,
	BarChart,
	colors,
	TriangleBar,
	Bar,
	Cell,
} from "recharts";

function Activities() {
	return (
		<div className=" pl-4 activities gap-3 p-7 h-full w-full">
			<div className="bg-sky-400 "> helo </div>
			<div className="bg-zinc-400 "> helo </div>
			<div className="bg-green-400  "> helo </div>
			<div className="col-span-full flex gap-2 ">
				<div className="h-full backdrop-blur-md flex-1 w-full">
					<ResponsiveContainer
						width="100%"
						height="100%"
					>
						<LineChart
							data={most_view_post}
							height={20}
							width={20}
							margin={{}}
						>
							<CartesianGrid strokeDasharray="3 3" />
							<XAxis dataKey="name" />
							<YAxis />
							<Tooltip />
							<Legend />
							<Line
								type="monotone"
								dataKey="number_of_viewers"
								stroke="#8884d8"
								activeDot={{ r: 8 }}
							/>
						</LineChart>
					</ResponsiveContainer>
				</div>
				<div className="bg-white h-full flex-1 w-full">
					<BarChart
						width={500}
						height={300}
						data={most_view_post}
						margin={{
							top: 20,
							right: 30,
							left: 20,
							bottom: 5,
						}}
					>
						<CartesianGrid strokeDasharray="3 3" />
						<XAxis dataKey="name" />
						<YAxis />
						<Bar
							dataKey="number_of_viewers"
							fill="#8884d8"
							shape={<TriangleBar />}
							label={{ position: "top" }}
						>
							{most_view_post.map((entry, index) => (
								<Cell
									key={`cell-${index}`}
									fill={colors[index % 20]}
								/>
							))}
						</Bar>
					</BarChart>
				</div>
			</div>
		</div>
	);
}

export default Activities;

const most_view_post = [
	{
		id: 1,
		category: " education",
		number_of_viewers: 40,
	},
	{
		id: 2,
		category: " Religin",
		number_of_viewers: 40,
	},
	{
		id: 3,
		category: " technology",
		number_of_viewers: 89,
	},
	{
		id: 4,
		category: " Agriculture",
		number_of_viewers: 200,
	},
	{
		id: 5,
		category: "Politics",
		number_of_viewers: 37,
	},
];
